import { useState } from "react";

function App() {
  let [food, setFood] = useState(["노현호 튀김", "노현호 조림", "노현호 구이"]);
  const [like, setLike] = useState(0);

  const handleLikeClick = () => {
    setLike(like + 1);
  };

  const handleButtonClick = () => {
    let newArray = [...food];
    newArray[1] = "노현호 스페셜";
    setFood(newArray);
  };

  return (
    <div className="App">
      <div className="black-nav">
        <div>뚱이 블로그!!</div>
      </div>

      {food.map((a, i) => {
        return (
          <div className="list">
            <h4>
              {a}
              {i === 0 ? (
                <span onClick={handleLikeClick}>🤍{like}</span>
              ) : i === 1 ? (
                <button onClick={handleButtonClick}>스페셜 메뉴로 변경</button>
              ) : (
                <span>💜</span>
              )}
            </h4>
            {/* 세 번째 p태그에 대해서 onClick */}
            {i === 2 ? (
              <p onClick={handleButtonClick}>🤍{like}</p>
            ) : (
              <p>2월 {`${i + 10}`}일 발행</p>
            )}
            <hr />
          </div>
        );
      })}
      <Modal />
    </div>
  );
}

function Modal() {
  return (
    <div className="modal">
      <h4>제목</h4>
      <p>날짜</p>
      <p>상세내용</p>
    </div>
  );
}

export default App;
