import { useState } from "react";

function App() {
  let [food, setFood] = useState(["노현호 튀김", "노현호 조림", "노현호 구이"]);
  const [like, setLike] = useState(0);
  const [modal, setModal] = useState(false);
  const [togle, setTogle] = useState(false);

  const handleModalOpen = (i) => {
    setIndex(i);
    setModal(!modal);
  };

  const handleLikeClick = () => {
    setLike(like + 1);
  };

  const handleButtonClick = () => {
    if (togle) {
      let newArray = [...food];
      newArray[1] =
        "노현호 스페셜(노현호 칼국수 + 노현호 튀김 + 노현호 액젓 + 노현호 꼬지)";
      setFood(newArray);
    } else {
      let newArray = [...food];
      newArray[1] = "노현호 조림";
      setFood(newArray);
    }
    setTogle(!togle);
  };

  return (
    <div className="App">
      <div className="black-nav">
        <div>뚱이 블로그!!</div>
      </div>

      {food.map((a, i) => {
        return (
          <div className="list">
            <h4>
              {a}
              {i === 0 ? (
                <span onClick={handleLikeClick}>🤍{like}</span>
              ) : i === 1 ? (
                <button onClick={handleButtonClick}>
                  {togle ? "스페셜 메뉴로 변경" : "노현호 조림으로 변경"}
                </button>
              ) : (
                <span>💜</span>
              )}
            </h4>

            <p onClick={handleModalOpen(i)}>2월 {`${i + 10}`}일 발행</p>

            <hr />
          </div>
        );
      })}
      {modal && <Modal />}
    </div>
  );
}

function Modal() {
  return (
    <div className="modal">
      <h4>{"asdf"}</h4>
      <p>날짜</p>
      <p>상세내용</p>
    </div>
  );
}

export default App;
